var schema = require('./entities/projectStructure.js').data;
var autoIncrement = require('mongoose-auto-increment');
var mongoose = require('mongoose'),
    Schema = mongoose.Schema,
    ObjectId = Schema.ObjectId;

var connection = mongoose.createConnection("mongodb://localhost/ControlH3");
    autoIncrement.initialize(connection);
    
var projectSchema = new Schema(schema);

projectSchema.plugin(autoIncrement.plugin, 'project');
module.exports = mongoose.model('project', projectSchema);